package textadventure;import items.Container;/** * A Player can contain objects and knows the Room it's currently in. */public class Player extends Container {	/** Room this Player is in at the moment */	private Room currentRoom;	private int health;	private boolean hasBrushedTeeth;	private boolean isWearingClothes;		/** Creates a player that starts in the given Room with no items */	public Player(Room startRoom, World world, String name, String description) {		super(world, name, description);		currentRoom = startRoom;		health = 0;		hasBrushedTeeth = false;		isWearingClothes = false;	}	/* Getters and setters */	public Room getCurrentRoom() {		return currentRoom;	}	public void setCurrentRoom(Room newRoom) {		currentRoom = newRoom;	}	public int getHealth() {		return health;	}	public void setHealth(int health) {		this.health = health;	}	public boolean isHasBrushedTeeth() {		return hasBrushedTeeth;	}	public void setHasBrushedTeeth(boolean hasBrushedTeeth) {		this.hasBrushedTeeth = hasBrushedTeeth;	}	public boolean isWearingClothes() {		return isWearingClothes;	}	public void setIsWearingClothes(boolean wearingClothes) {		this.isWearingClothes = wearingClothes;	}}